    public String getParentTrans() {
       String parent= (String) getAttributeInternal(PARENTTRANS);
       
       if(parent == null){
           LovScheduleLineVOImpl lineVo=(LovScheduleLineVOImpl)this.getLovScheduleLineVO1().getViewObject();
           
           if( getSchLineIdPrnt()!= null && (!getSchLineIdPrnt().equals("00"))){
            lineVo.setBindCldId(this.getSchCldId());
            lineVo.setBindHoOrgId(this.getSchHoOrgId());
            lineVo.setBindScheduleId(this.getSchId());
               lineVo.setBindLineId(getSchLineIdPrnt());
            lineVo.setBindScheduleDisc(null);
            
            //System.out.println("Parent id is "+getSchLineIdPrnt() +" cld Id is "+ this.getSchCldId()+ " line id "+this.getSchLineId() + " ho Org id "+this.getSchHoOrgId());
               lineVo.executeQuery();
            //   System.out.println("Row count is "+lineVo.getRowCount());
               parent=((LovScheduleLineVORowImpl)lineVo.first()).getSchLineDesc();
           }else{
               lineVo.setBindLineId(null);
                  lineVo.executeQuery();  
           }
       }
        
       return parent; 
    }
    

