public class MmQcParamItmVOImpl extends ViewObjectImpl implements MmQcParamItmVO {
    /**
     * This is the default constructor (do not remove).
     */
    public MmQcParamItmVOImpl() {
    }
    public int isParamDuplicate(String value){
        int totalCount = this.getRowCount(); //get ALL rows
        int rangeSize = this.getRangeSize(); //all in range
        this.setRangeSize(totalCount);
        Row[] rArray = this.getAllRowsInRange();
        
        Row cRow = this.getCurrentRow();
        int count = 0;
        String currLang = "";

        for (Row r : rArray) {

            if (!r.equals(cRow)) {
                try {
                    currLang = r.getAttribute("ParamId").toString();
                } catch (NullPointerException npe) {
                          npe.printStackTrace();
                    currLang = "";
                }
                if (currLang.equalsIgnoreCase(value)) {
                    count = count + 1;
                }
            }
          
        }
        System.out.println("count in itm vo impl   "+count);
        this.setRangeSize(rangeSize); //set to original range
        return count;
    }
}
