    /*Method for insert data OrgMnfOP Table*/
    public void CreateInMnfOrg() {
        String Activ = "Y";
        String org_id = resolvEl("#{pageFlowScope.GLBL_APP_USR_ORG}").toString();
        Integer user_id_create = Integer.parseInt(resolvEl("#{pageFlowScope.GLBL_APP_USR}").toString());
        Row r1 = getOrgMnfOp().createRow();
        r1.setAttribute("OrgId", org_id);
        r1.setAttribute("DocId", getDocId());
        r1.setAttribute("OpActv", Activ);
        r1.setAttribute("UsrIdCreate", user_id_create);
        r1.setAttribute("UsrIdCreateDt", new Timestamp(System.currentTimeMillis()));
        // r1.setAttribute("InactvDt", getOpInactvDt());
        // r1.setAttribute("InactvReason", getOpInactvReason());
        getOrgMnfOp().insertRow(r1);
    }

